require 'bcrypt'
class User < ActiveRecord::Base
  attr_accessible :email, :password, :fname, :mname, :lname, :password_confirmation, :login, 
                    :role, :addr1, :addr2, :addr3, :addr4, :phone1, :phone2, :sex,
                    :adhaar_no, :verified, 
                    :active, :approved, :confirmed, :ndelete, :created_by, :updated_by, 
                    :avatar, :community_id

  include ActiveFlag
  include AclCheckOnRole

  has_many :user_house_links
  has_many :houses, through: :user_house_links
  has_many :notifications
  has_many :communities, class_name: "Community", foreign_key: "manager_id"
  has_many :user_house_contracts
  belongs_to :community
  
  module USER_AVATAR_SETTINGS
    DEFAULT_AVATAR_URL = "/system/:attachment/default.png" #"/images/:style/missing.png"
    PROCESSED_DEFAULT_AVATAR_URL = "/system/avatars/default.png"
    LOCATION = ":rails_root/public/system/:attachment/:username.:extension"
    DEFAULT_AVATAR=""
    DEFAULT_AVATAR_FILENAME="default.png"
    DEFAULT_AVATAR_FILETYPE="image/png"
  end
  
  #default location of avatars are /Users/abedali/eclipse/workspace1/h4r_backend/public/system/users/avatars/000/000/001  
  has_attached_file :avatar, 
                    #:path => USER_AVATAR_SETTINGS::LOCATION,
                    #:url  => "/:attachment/:username.:extension",
                    styles: { medium: "300x300>", thumb: "100x100>" }, default_url: USER_AVATAR_SETTINGS::DEFAULT_AVATAR_URL
  #validates_with AttachmentSizeValidator, attributes: :avatar, less_than: 1.megabytes
  validates_attachment :avatar, 
        content_type: { content_type: ["image/jpeg", "image/gif", "image/png"] }, 
        size: { in: 0..1.megabytes }
  
  module USER_SEX
    DEFAULT       = 0,
    MALE          = 1,
    FEMALE        = 2
  end
  
  #belongs_to :created_by_user, :foreign_key => 'created_by'
  #belongs_to :updated_by_user, :foreign_key => 'updated_by'
  
#  SEED_OPTS = {
#  :organizations => 50,
#}

  include BCrypt
  acts_as_authentic do |c|
    #c.my_config_option = my_value
    #c.login_field = :username
    c.crypto_provider = Authlogic::CryptoProviders::BCrypt
    c.validates_format_of_email_field_options = {:with => Authlogic::Regex.email_nonascii}
  end # the configuration block is optional
  
  def otherRole?
    !(admin? && guest? && tenant? && land_lord? && accountant? &&
      property_mgmt_mgr? && property_mgmt_emp? && agency_collection_emp? && agency_collection_mgr?)
  end
  
  #check if the user is a owner of this house (param)
  def land_lord? (house)
    user_house_links.each do |user_house_link|
            return true if !user_house_link.house.nil? && user_house_link.house.id == house.id &&
                     user_house_link.land_lord?
    end
    return false
  end
  
  #check if the user is a owner of this house (param)
  def accountant? (house)
    user_house_links.each do |user_house_link|
            return true if !user_house_link.house.nil? && user_house_link.house.id == house.id &&
                     user_house_link.accountant?
    end
    return false
  end
  
  #check if the user is a manager of community(param)
  def manager? (community)
    community.manager_id == id
  end
  
  #check if the user has created this community(param)
  def created? (community)
    community.created_by == id
  end
  
  def house_created? (house)
    house.created_by == id
  end
  
  def verified?
    verified
  end
  def male?
    sex == USER_SEX::MALE
  end
  
  def female?
    sex == USER_SEX::FEMALE
  end
  
  def active?
    active
  end
  
  def approved?
    approved
  end
  
  def confirmed?
    confirmed
  end
  
  def fullName 
    lname + ", " + fname
  end
#  def activate!
#    update_attributes(:active => true) #, :approved =>true, :confirmed => true)
#  end

  def activate!
    self.active = true
    save
  end
  
  def approve!
    self.approved = true
    save
  end
  
  def confirm!
    self.confirmed = true
    save
  end
  
  def inactive?
    ndelete
  end
  
  def deactivate!
    self.ndelete = true
    save
  end
  
  def self.isDefaultAvatar (url)
    return url == USER_AVATAR_SETTINGS::PROCESSED_DEFAULT_AVATAR_URL
  end
  
  #def password
  #  @password ||= Password.new(password_hash)
  #end
  
  #def password=(new_password)
  #  @password = Password.create(new_password)
  #  self.password_hash = @password
  #end
  
end